const token = JSON.parse($input.first().json.data)
const request = {
  url: "https://api.desk.ms/AutoCategorias/lista",
  method: "POST",
  headers: {
    "Authorization": token,
    "Content-Type": "application/json"
  },
  body: {
    "Pesquisa": "",
    "Ativo": "S"
  }
};

const response = await this.helpers.httpRequest(request);
const data = response.root || [];

// Filtra e transforma os dados
const resultado = data
  .filter(item => typeof item.Assunto === 'string' && item.Assunto.includes(' - '))
  .map(item => {
    const partes = item.Assunto.split(' - ').map(p => p.trim());

    return {
      chave: item.Chave || null,
      categoria: partes[0] || '',
      subcategoria: partes[1] || '',
      tipo: partes[2] || ''
    };
  });

return resultado.map(item => ({ json: item }));

