const token = JSON.parse($input.first().json.data);

// 1. Buscar todos os grupos
const requestGroupsList = {
  url: "https://api.desk.ms/GrupoEmpresas/lista",
  method: "POST",
  headers: {
    "Authorization": token,
    "Content-Type": "application/json"
  },
  body: {
    "Pesquisa": "",
    "Ativo": "S"
  }
};

const responseGroupLists = await this.helpers.httpRequest(requestGroupsList);
const groupLists = responseGroupLists.root || [];

// 2. Para cada grupo, buscar as empresas e montar a lista Ãºnica de empresas+grupo
let empresas = [];

for (const grupo of groupLists) {
  const requestEmpresas = {
    url: "https://api.desk.ms/GrupoEmpresas",
    method: "POST",
    headers: {
      "Authorization": token,
      "Content-Type": "application/json"
    },
    body: {
      "Chave": grupo.Sequencia
    }
  };

  const responseEmpresas = await this.helpers.httpRequest(requestEmpresas);
  const infoGrupo = responseEmpresas.TGrupoEmpresa || {};
  const listaEmpresas = infoGrupo.ListaEmpresas || [];

  listaEmpresas.forEach(emp => {
    empresas.push({
      empresa_id: emp.id,
      name: emp.text,
      grupo_nome: grupo.Nome,
      codigo_sequencia: grupo.Sequencia
    });
  });
}

// 3. Retornar uma empresa por item
return empresas.map(item => ({ json: item }));

