// 1. Lista de usuÃ¡rios
const requestList = {
  url: "https://api.desk.ms/Usuarios/lista",
  method: "POST",
  headers: {
    "Authorization": token,
    "Content-Type": "application/json"
  },
  body: {
    "Pesquisa": "",
    "Ativo": "S"
  },
  json: true
};

const responseList = await this.helpers.httpRequest(requestList);
const data = responseList.root || [];

// 2. Faz requisiÃ§Ãµes em paralelo usando Promise.all
const resultados = await Promise.all(data.map(async (item) => {
  const chave = item.Chave;

  const requestDetail = {
    url: "https://api.desk.ms/Usuarios",
    method: "POST",
    headers: {
      "Authorization": token,
      "Content-Type": "application/json"
    },
    body: {
      Chave: chave
    },
    json: true
  };

  try {
    const responseDetail = await this.helpers.httpRequest(requestDetail);
    const telefone = responseDetail?.TUsuario?.SMSNumero || "";

    return {
      json: {
        chave: item.Chave,
        codigo_cliente: item.CodigoCliente,
        cliente: item.Cliente,
        email: item.Email,
        nome: item.Nome,
        sobrenome: item.Sobrenome,
        telefone
      }
    };
  } catch (err) {
    return {
      json: {
        chave: item.Chave,
        codigo_cliente: item.CodigoCliente,
        cliente: item.Cliente,
        email: item.Email,
        nome: item.Nome,
        sobrenome: item.Sobrenome,
        telefone: ""
      }
    };
  }
}));

return resultados;

