parameters.jsCode:
let formattedJSON = {
  company_name: $input.first().json.body.event.razao_social ?? "",
  fantasy_name: $input.first().json.body.event.nome_fantasia ?? "",
  address: $input.first().json.body.event.endereco ?? "",
  number: $input.first().json.body.event.endereco_numero ?? null,
  neighborhood: $input.first().json.body.event.bairro ?? "",
  zip_code: $input.first().json.body.event.cep ?? null,
  city: $input.first().json.body.event.cidade ?? "",
  state: $input.first().json.body.event.estado ?? "",
  enabled: 1,
  blocked: 0
}

function limparCPF(cpf) {
  return cpf.replace(/[\.\-]/g, '');
}

function limparCNPJ(cnpj) {
  return cnpj.replace(/[\.\/\-]/g, '');
}

if($input.first().json.body.event.cnpj_cpf && $input.first().json.body.event.cnpj_cpf.length === 14){

  formattedJSON.cpf = limparCPF($input.first().json.body.event.cnpj_cpf)
} else if ($input.first().json.body.event.cnpj_cpf) {
  formattedJSON.cnpj = limparCNPJ($input.first().json.body.event.cnpj_cpf)
}

if($input.first().json.body.event.complemento){
  formattedJSON.complement = $input.first().json.body.event.complemento
}

if($input.first().json.body.event.contato){
  formattedJSON.contact = $input.first().json.body.event.contato
}

if($input.first().json.body.event.email){
  formattedJSON.email = $input.first().json.body.event.email
}

if($input.first().json.body.event.homepage){
  formattedJSON.site = $input.first().json.body.event.homepage
}

if($input.first().json.body.event.observacao){
  formattedJSON.obs = $input.first().json.body.event.observacao
}

if($input.first().json.body.event.telefone1_ddd && $input.first().json.body.event.telefone1_numero){
  formattedJSON.phone_1 = $input.first().json.body.event.telefone1_ddd + $input.first().json.body.event.telefone1_numero
}

if($input.first().json.body.event.telefone2_ddd && $input.first().json.body.event.telefone2_numero){
  formattedJSON.phone_2 = $input.first().json.body.event.telefone2_ddd + $input.first().json.body.event.telefone2_numero
}

if($input.first().json.body.event.telefone3_ddd && $input.first().json.body.event.telefone3_numero){
  formattedJSON.phone_3 = $input.first().json.body.event.telefone3_ddd + $input.first().json.body.event.telefone3_numero
}


return formattedJSON

async function saveClientDesk() {
  const requestData = {
    url: "https://api.desk.ms/Clientes",
    method: "PUT",
    headers: {
      "Authorization": $input.first().json.data,
      "Content-Type": "application/json",
    },
    body: {
      TCliente: $input.last().json.TCliente,
      TCatalogos: $input.last().json.TCatalogos,
      TBloqueioIp: $input.last().json.TBloqueioIp,
      TCampoExtraGeral: $input.last().json.TCampoExtraGeral,
      TCampoExtraVisualiza: $input.last().json.TCampoExtraVisualiza,
    },
  };

  const response = await this.helpers.httpRequest(requestData);

  return [{ data: response }];
}

return saveClientDesk();
