const codigo_lancamento_omie = items[0].json["body"]["event"]["codigo_lancamento_omie"];

const boleto_numero = items[0].json["body"]["event"]["boleto_numero"];

return [{ json: { dataFormatada, codigoClienteFornecedor, valor_documento, codigo_lancamento_omie, boleto_numero } }];

let formattedJSON = {
  company_name: items[0].json["razao_social"],
  fantasy_name: items[0].json["nome_fantasia"],
  address: items[0].json["endereco"],
  number: items[0].json["endereco_numero"],
  neighborhood: items[0].json["bairro"],
  zip_code: items[0].json["cep"],
  city: items[0].json["cidade"],
  state: items[0].json["estado"],
  enabled: 1,
  blocked: 0
}

if(items[0].json["cnpj_cpf"].length === 14){
  formattedJSON.cpf = items[0].json["cnpj_cpf"]
} else {
  formattedJSON.cnpj = items[0].json["cnpj_cpf"]
}

if(items[0].json["complemento"]){
  formattedJSON.complement = items[0].json["complemento"]
}

if(items[0].json["contato"]){
  formattedJSON.contact = items[0].json["contato"]
}

if(items[0].json["email"]){
  formattedJSON.email = items[0].json["email"]
}

if(items[0].json["homepage"]){
  formattedJSON.site = items[0].json["homepage"]
}

if(items[0].json["observacao"]){
  formattedJSON.obs = items[0].json["observacao"]
}

if(items[0].json["telefone1_ddd"] && items[0].json["telefone1_numero"]){
  formattedJSON.phone_1 = items[0].json["telefone1_ddd"] + items[0].json["telefone1_numero"]
}

if(items[0].json["telefone2_ddd"] && items[0].json["telefone2_numero"]){
  formattedJSON.phone_2 = items[0].json["telefone2_ddd"] + items[0].json["telefone2_numero"]
}

if(items[0].json["telefone3_ddd"] && items[0].json["telefone3_numero"]){
  formattedJSON.phone_3 = items[0].json["telefone3_ddd"] + items[0].json["telefone3_numero"]
}


return formattedJSON

