const token = $('getToken').first().json.response
const chamadoPai = $('VARS').first().json.content.pai
const lastDescription = $('VARS').first().json.content.descr
const internalDescription = $('VARS').first().json.content.descrinterna

async function getMainTicketId(){
  const request = {
    url: "https://api.desk.ms/ChamadosSuporte/lista",
    method: "POST",
    headers: {
      "Authorization": token,
      "Content-Type": "application/json"
    },
    body: {
      "Pesquisa": chamadoPai,
      "Colunas": {
        "Chave": "on",
        "CodChamado": "on"
      }
    }
  }

  const response = await this.helpers.httpRequest(request);
  return response.root[0]?.Chave
}

async function openTicket(){
  const request = {
    url: "https://api.desk.ms/ChamadosSuporte",
    method: "PUT", 
    headers: {
      "Authorization": token,
      "Content-Type": "application/json"
    },
    body: {
      "TChamado": {
        "Solicitante": "1945",
        "AutoCategoria": "008208",
        "Solicitacao": "000071",
        "ObservacaoInterna": `${internalDescription}`,
        "Descricao": `Última descrição: \n${lastDescription}`
      }
    }
  }
  const response = await this.helpers.httpRequest(request);
  return response[0]
}

async function getOpenTicketKey(codigoChamado){
  const request = {
    url: "https://api.desk.ms/ChamadosSuporte/lista",
    method: "POST",
    headers: {
      "Authorization": token,
      "Content-Type": "application/json"
    },
    body: {
      "Pesquisa": codigoChamado,
      "Colunas": {
        "Chave": "on",
        "CodChamado": "on"
      }
    }
  };

  const response = await this.helpers.httpRequest(request);
  return response.root[0].Chave;
}
async function linkTickets(mainTicketId, chaveFilho) {
  const request = {
    url: "https://api.desk.ms/ChamadosSuporte/vincular",
    method: "PUT",
    headers: {
      "Authorization": token,
      "Content-Type": "application/json"
    },
    body: {
      "TChamadoPai": {
        "CodChamadoPai": mainTicketId
      },
      "TChamadoFilho": [
        {
          "CodChamadoFilho": chaveFilho,
          "Ex": "0"
        }
      ]
    }
  };

  const response = await this.helpers.httpRequest(request);
  return response;
}
return (async () => {
  const mainTicketId = await getMainTicketId();
  const codChamado = await openTicket();

  if (!codChamado) throw new Error("Código do chamado não encontrado");

  const chaveFilho = await getOpenTicketKey(codChamado);
  if (!chaveFilho) throw new Error("Chave do chamado filho não encontrada");

  const linkResponse = await linkTickets(mainTicketId, chaveFilho);

  return [{
    mainTicketId: mainTicketId,
    codChamado: codChamado,
    chaveFilho: chaveFilho,
    linkResponse: linkResponse
  }]
})();

