  const operatorId = await getOperatorId(fullName);
  if (!operatorId) throw new Error("Operador não encontrado");

  const interactionResponse = await operatorMainTicket(mainTicketId, operatorId);

  return [{
    mainTicketId: mainTicketId,
    fullName: fullName,
    operatorId: operatorId,
    interactionResponse: interactionResponse
  }]
})()

const tokenAuthenticate = $('getToken').first().json.response
const chave = $('VARS').first().json.content.chamado_cod

async function getAutoCategory(){
  const request = {
    url: "https://api.desk.ms/ChamadosSuporte",
    method: "POST",
    headers: {
      "Authorization": tokenAuthenticate,
      "Content-Type": "application/json"
    },
    body: {
      "Chave": chave
    }
  }
  const response = await this.helpers.httpRequest(request)
  return response.TChamado.Assunto
}

async function listInteractions(){
  try {
    const category = await getAutoCategory()
    let isWithoutContract = false
    if (category.startsWith("Sem Contrato")) {
      const keyWords = ["Orçamento", "Dúvida", "Cortesia"]
      const containsKeyWord = keyWords.some(keyWord => category.includes(keyWord))

      if(!containsKeyWord){
        isWithoutContract = true
      }
  }

  const request = {
    url: "https://api.desk.ms/ChamadoHistoricos/lista",
    method: "POST", 
    headers: {
      "Authorization": tokenAuthenticate,
      "Content-Type": "application/json"
    },
    body: {
      "Chave": chave,
      "Solicitante": "N"
    }
  }
  const response = await this.helpers.httpRequest(request)
  const interactions = response.root || []

  const statusCode = ["000182", "000030"];

  const isStatusTarget = interactions.some(interaction =>
      interaction.Status?.some(status => statusCode.includes(status.sequence))
    );

    const totalSeconds = interactions.reduce((total, interaction) => {
      const [h, m, s] = interaction.TotalHoras?.split(":").map(Number) || [0, 0, 0];
      return total + (h * 3600 + m * 60 + s);
    }, 0);

    const hours = Math.floor(totalSeconds / 3600);
    const minutes = Math.floor((totalSeconds % 3600) / 60);
    const totalFormatted = `${String(hours).padStart(2, '0')}:${String(minutes).padStart(2, '0')}`;

    const matchedInteraction = interactions.find(i =>
      i.Status?.some(status => status.sequence === "000182")
    );
    const isReceivedPart = matchedInteraction?.Descricao || ""

    const hasOnsiteVisit = interactions?.some(i => (i?.NomeFormaAtendimento || '').trim() === "Visita Técnica")
    let shouldChargeVisit = false
    if(hasOnsiteVisit){
      const ticketResp = await this.helpers.httpRequest({
        url: "https://api.desk.ms/ChamadosSuporte",
        method: "POST",
        headers: {
          "Authorization": tokenAuthenticate,
          "Content-Type": "application/json"
        },
        body: {
          "Chave": chave
        }
      })
      const clientId = ticketResp?.TChamado?.CodCliente?.[0]?.id

      if(clientId){
        const clientResp = await this.helpers.httpRequest({
            url: "https://api.desk.ms/Clientes",
            method: "POST",
            headers: {
              "Authorization": tokenAuthenticate,
              "Content-Type": "application/json"
            },
            body: {
              "Chave": clientId
            }
          })
        const city = (clientResp?.TCliente?.Cidade || "").trim().toLowerCase()
        if(city !== "botucatu"){
          shouldChargeVisit = true
        }
      }
      }

    return [{
      isStatusTarget: isStatusTarget,
      totalHours: totalFormatted,
      isReceivedPart: isReceivedPart,
      isWithoutContract: isWithoutContract,
      shouldChargeVisit: shouldChargeVisit
    }]

  } catch (error) {
    console.error("Erro ao listar interações:", error.message);
    return [{ error: true, message: error.message }];
  }
}

return listInteractions();

