async function getAssuntos(categoria, codigoCliente) {
  const request = {
    url: "https://api.desk.ms/ChamadosSuporte/ListaAutocategoria",
    method: "POST",
    headers: {
      "Authorization": token,
      "Content-Type": "application/json"
    },
    body: {
      "Pesquisa": categoria,
      "Ativo": "S",
      "Filtro": {
        "ListaCatalogoUsuario": [codigoCliente, "equal"]
      }
    }
  };

  const response = await this.helpers.httpRequest(request);

  const normalize = (s) => (s ?? "")
    .toString()
    .trim()
    .toLowerCase()
    .normalize("NFD")
    .replace(/[\u0300-\u036f]/g, "");

  const q = normalize(categoria);
  const hasExactPrefix =
    Array.isArray(response?.root) &&
    response.root.some(item => {
      const assunto = normalize(item?.Assunto);
      return assunto.startsWith(q);
    });

  return hasExactPrefix ? categoria : null;
}

async function main() {
  const promises = items.map(item => getAssuntos(item.json.categoria, solicitanteId));
  const categoriasComChave = await Promise.all(promises);

  const categoriasFiltradas = categoriasComChave.filter(Boolean);

  return {
    json: {
      categorias: categoriasFiltradas
    }
  };
}

return main();

