const chaveGrupo = $('Merge').first().json.chaveGrupo;
const token = JSON.parse($input.first().json.data);

async function fetchChatMessages(chatId) {
  const requestOptions = {
    url: `https://api.stw.chat/core/v2/api/chats/${chatId}`,
    method: 'GET',
    headers: {
      'Content-Type': 'application/json',
      'access-token': '689a61a95706d77b7eb722cb'
    }
  };

  const response = await this.helpers.httpRequest(requestOptions);
  return response.messages || [];
}

function formatDescricao(messages) {
  return messages
    .filter(msg => !msg.isSystemMessage && !msg.isPrivate)
    .map(msg => {
      const remetente = msg.isSentByMe ? 'Técnico' : msg.senderName;
      const date = new Date(msg.dhMessage || msg.utcDhMessage);
      const dataHorario = date.toLocaleString('pt-BR', {
        day: '2-digit',
        month: '2-digit',
        year: 'numeric',
        hour: '2-digit',
        minute: '2-digit',
        hour12: false
      });

      if (msg.dataMedia && msg.dataMedia.urlFile && msg.dataMedia.mimetype) {
        const tipo = (msg.dataMedia.mimetype.split('/')[1] || 'arquivo');
        const nome = msg.dataMedia.filename || 'arquivo';
        return `[${dataHorario}] ${remetente}:\n[${tipo}] ${nome} - ${msg.dataMedia.urlFile}`;
      }
      return `[${dataHorario}] ${remetente}:\n${msg.text || ''}`;
    })
    .join('\n\n');
}

async function downloadAsBase64(url) {
  const response = await this.helpers.request({
    method: 'GET',
    url: url,
    encoding: null
  });
  return Buffer.from(response).toString('base64');
}

async function openTicket(descricao) {
  const options = {
    url: 'https://api.desk.ms/ChamadosSuporte',
    method: 'PUT',
    headers: {
      'Content-Type': 'application/json',
      'Authorization': token
    },
    body: {
      TChamado: {
        Solicitante: chaveSolicitante,
        AutoCategoria: chaveAutocategoria,
        Solicitacao: "000060",
        Descricao: descricao,
        ObservacaoInterna: bodyData.obsInterna || "",
        TransfCodGrupo: chaveGrupo || "",
        TransfCodOperador: chaveOperador || ""
      }
    },
    json: true
  };

  return await this.helpers.httpRequest(options);
}

async function enviarAnexo(anexo, chamado) {
  const request = {
    url: "https://files.desk.ms/v1/enviar",
    method: "PUT",
    headers: {
      "Authorization": token,
      "Content-Type": "application/json"
    },
    body: {
      "chamado": chamado,
      "base64": anexo.base64,
      "type": anexo.mimetype,
      "name": anexo.filename
    }
  };
  const response = await this.helpers.httpRequest(request);
  return response;
}

async function sendSuccesfullMessage(ticket){
  const options = {
    url: 'https://api.stw.chat/core/v2/api/chats/send-text',
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      'access-token': "689a61a95706d77b7eb722cb"
    },
    body: {
      "forceSend": true,
      "isWhisper": false,
      "message": `Chamado criado com sucesso!\nO número do seu chamado é: ${ticket}`,
      "number": bodyData.number,
      "verifyContact": false
    },
    json: true
  };

  return await this.helpers.httpRequest(options);
}

const chatId = bodyData.chatId;
const messages = await fetchChatMessages(chatId);
const descricao = formatDescricao(messages);
const chamadoResponse = await openTicket(descricao);
const chamado = chamadoResponse[0]
const sendMessage = await sendSuccesfullMessage(chamado)

const anexos = [];
for (const msg of messages) {
  if (msg.dataMedia && msg.dataMedia.urlFile) {
    const base64 = await downloadAsBase64.call(this, msg.dataMedia.urlFile);
    anexos.push({
      base64: base64,
      filename: msg.dataMedia.filename || 'anexo',
      mimetype: msg.dataMedia.mimetype || 'application/octet-stream'
    });
  }
}

const anexosResultados = [];
for (const anexo of anexos) {
  if (chamado) {
    const resultado = await enviarAnexo(anexo, chamado);
    anexosResultados.push(resultado);
  }
}

return [{
  json: {
    descricao,
    chamado: chamadoResponse,
    anexosEnviados: anexosResultados,
    sendMessage
  }
}];

