async function getOperatorInfo(){
  const request = {
    url: "https://api.desk.ms/ChamadosSuporte/lista",
    method: "POST",
    headers: {
      "Authorization": token,
      "Content-Type": "application/json"
    },
    body: {
      "Pesquisa": chamado,
      "Colunas": {
        "NomeOperador":"on",
        "SobrenomeOperador":"on",
        "SequenciaGrupo": "on"
      }
    }
  }
  const response = await this.helpers.httpRequest(request);  
  const ticket = response.root[0];
  const nome = ticket?.NomeOperador || "";
  const sobrenome = ticket?.SobrenomeOperador || "";
  const nomeCompleto = [nome, sobrenome].filter(Boolean).join(" ").trim();
  const sequenciaGrupo = ticket?.SequenciaGrupo || "";
  return { nomeCompleto, sequenciaGrupo };
}

