      "Ativo": "S"
    }
  }

  const response = await this.helpers.httpRequest(request)
  return response.root[0]?.Chave
}

async function interactTicket(fields){
  const request = {
    url: "https://api.desk.ms/ChamadosSuporte/interagir",
    method: "PUT",
    headers: {
      "Authorization": token,
      "Content-Type": "application/json"
    },
    body: {
      "Chave": chamado,
      "TChamado":{
        "CodFormaAtendimento": "000004",
        "CodStatus": status,
        ...(fields.transferirOperador && { "TransferirOperador": fields.transferirOperador }),
        ...(fields.transferirGrupo && { "TransferirGrupo": fields.transferirGrupo }),
        ...(fields.codOperador && { "CodOperador": fields.codOperador }),
        "Descricao": fields.descricao,
        "ObservacaoInterna": bodyData.obsInterna
      }
    }
  }

  const response = await this.helpers.httpRequest(request)
  return response
}

