  if (operatorInfo.nomeCompleto) {
    codOperador = await getOperatorId(operatorInfo.nomeCompleto) || "";
  } else if (operatorInfo.sequenciaGrupo) {
    transferirGrupo = operatorInfo.sequenciaGrupo;
  }
}

const fields = {
