const chave = $('Execute a SQL query').first().json.chave
const token = JSON.parse($input.first().json.data);
const chatId = $('Webhook').first().json.body.data.chatId
const number = $('Webhook').first().json.body.data.number

async function fetchChatMessages() {
  const requestOptions = {
    url: `https://api.stw.chat/core/v2/api/chats/${chatId}`,
    method: 'GET',
    headers: {
      'Content-Type': 'application/json',
      'access-token': '689a61a95706d77b7eb722cb'
    }
  };

  const response = await this.helpers.httpRequest(requestOptions);
  return response.messages || [];
}

function formatDescricaoSimples(messages) {
  return messages
    .filter(msg => !msg.isSystemMessage && !msg.isPrivate && msg.text)
    .map(msg => {
      const remetente = msg.isSentByMe ? 'Técnico' : msg.senderName;
      const date = new Date(msg.dhMessage);
      const dataHorario = date.toLocaleTimeString('pt-BR', {
        day: '2-digit',
        month: '2-digit',
        year: 'numeric',
        hour: '2-digit',
        minute: '2-digit',
        hour12: false
      });
      return `[${dataHorario}] ${remetente}:\n ${msg.text}`;
    })
    .join('\n\n');
}

async function openTicket(descricao) {
  const options = {
    url: 'https://api.desk.ms/ChamadosSuporte',
    method: 'PUT',
    headers: {
      'Content-Type': 'application/json',
      'Authorization': token
    },
    body: {
      TChamado: {
        Solicitante: chave,
        AutoCategoria: "8305",
        Solicitacao: "000105",
        Descricao: descricao
      }
    },
    json: true
  };

  return await this.helpers.httpRequest(options);
}

async function sendSuccesfullMessage(ticket){
  const options = {
    url: 'https://api.stw.chat/core/v2/api/chats/send-text',
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      'access-token': "689a61a95706d77b7eb722cb"
    },
    body: {
      "forceSend": true,
      "isWhisper": false,
      "message": `O chamado #${ticket} foi criado com sucesso e o atendimento por este canal está encerrado!\nVamos verificar sua solicitação, então por favor aguarde até retornarmos o contato :)`,
      "number": number,
      "verifyContact": false
    },
    json: true
  };

  return await this.helpers.httpRequest(options);
}

async function finalizeChat() {
  const requestOptions = {
    url: `https://api.stw.chat/core/v2/api/chats/${chatId}/finalize`,
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      'access-token': '689a61a95706d77b7eb722cb'
    },
    body: {
      "sendMessageFinalized": false,
      "fidelityUser": false,
      "sendResearchSatisfaction": false
    },
    json: true
  };

  const response = await this.helpers.httpRequest(requestOptions);
  return response;
}

const mensagens = await fetchChatMessages();
const descricao = formatDescricaoSimples(mensagens);
const chamado = await openTicket(descricao)
const numeroChamado = Array.isArray(chamado) && chamado.length > 0 ? chamado[0] : null;

if (!numeroChamado) {
  throw new Error("Número do chamado não retornado corretamente.");
}
const mensagemEnviada = await sendSuccesfullMessage(numeroChamado);
const finalize = await finalizeChat();

return [{
  json: {
    chamado,
    mensagemEnviada,
    finalize
  }
}];
